public class ArryIntro {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		// creat an arry
		
		int [] b = new int[4];
		 // store the values
		b[0] =90;
		b[1] = 85;
		b[2] = 70;
		b[3] = 100;

		// access values from my arry
		System.out.println(b[2]);
		System.out.println(b[1] + b[2] +b[3]);
		
		
		// we need to creat an arry of my classmates
		// when we creat an arry we must specify the size ( how many elements we store)
		// how values are stored --> stored based on index.
		
		
		String[] name = new String[5];
		//name[0]  = "Khrystna";
		name[1] = "Aimal";
		name[2] = "Mohammad";
		name[3] = "Arif";
		name[4]	= "Assel";	
		
		
		System.out.println("My classmeats " + name[0]);
		
		
		
		
		
		
		
		
	}

}
